import React, { Component } from 'react';
import {BrowserRouter, Route} from 'react-router-dom';
import './App.css';

import Summary from './components/Summary/Summary';

import Buttons from './components/Buttons/Buttons';
import ButtonOne from './components/ButtonOne/ButtonOne';


class App extends Component {
  state = {
    visible: true
  };




  //clickHandle = () => {
  //display('none');
  //}


  render(){
    const style = {
      padding: '20px'
    }

    const buttonText = this.state.visible ? "hide": "show";

  return (
    <BrowserRouter>
    <div className="App">

    <header>
      <nav>
        <ul className = "menu">
          <li className = 'mainmenu'><a className = 'menufont' href = "/">Home</a></li>
          <li className = 'mainmenu'><a className = 'menufont' href = "/">Blog</a></li>
          <li className = 'mainmenu'><a
          onClick ={() =>{
            this.setState({visible: false});
          }}
          className = 'menufont' href = "/Summary">About</a></li>
          <li className = 'mainmenu'><a className = 'menufont' href = "/">Contact</a></li>
        </ul>
      </nav>
    </header>

    <h1>CloudWhich.com</h1>
        <h2>Which cloud is for you?</h2>

    <br/>
    <br/>
    <nav className = 'nav'>
    {this.state.visible ? <ButtonOne/> : null}

</nav>


    </div>
    <Route path = "/Button" exact component={Buttons} />
    <Route path = "/Summary" exact component={Summary} />
</BrowserRouter>



  );

}
}

export default App;
